App.AudioView=App.SmartView.extend({tagName:"audio",className:"audio-view",attributeBindings:["src"],autoplay:false,preload:false,src:undefined,isSrcObservedPending:false,didInsertElement:function(){var a=this.get("element");a.addEventListener("canplaythrough",function(){a.removeEventListener("canplaythrough");a.audioLoaded=true},false);if(isSrcObservedPending){this.srcObserved()}},srcObserved:function(){isSrcObservedPending=false;var a=this.get("element");if(a){a.play();setTimeout(function(){a.pause()},0)}else{isSrcObservedPending=true}}.observes("src"),play:function(){this.get("element").play()}});